$(function () {
    $.fn.dataTable.moment('D/M/YYYY');
    //var request;
     $('#date_submit').click(function (){
         searchdate({
             'startDate':$('#reservation').val().split('-')[0].trim().replace(/(\d\d)\/(\d\d)\/(\d{4})/, "$3-$1-$2"),
             'endDate':$('#reservation').val().split('-')[1].trim().replace(/(\d\d)\/(\d\d)\/(\d{4})/, "$3-$1-$2"),
         });

     });
    function afterTwo(num){
        return (Math.round(num * 100) / 100).toFixed(2);
    }

    function dateformatChange(data){
        var date= data;
        var d=new Date(date.split("/").reverse().join("-"));
        var dd=d.getDate();
        var mm=d.getMonth()+1;
        var yy=d.getFullYear();
        var newdate=dd+"/"+mm+"/"+yy;
        return newdate;
    }
    fetch('no');
    function searchdate(data){
        $('#dieselTable').DataTable().destroy();
        setTimeout(() => {
            fetch('Yes',data);
        }, 1);

    }
    function fetch(yesno,data){
        $('#dieselTable').DataTable({
            dom: 'lBfrtip',
            order: [[ 0, "desc" ]],
            buttons: [
                { extend: 'copyHtml5', footer: true,
                    filename: function(){
                        var d = new Date();
                        var n = d.getTime();
                        return 'diesel' +'-'+d+'-'+ n;
                    },
                },
                { extend: 'excelHtml5', footer: true,
                    orientation: 'landscape',
                    filename: function(){
                        var d = new Date();
                        var n = d.getTime();
                        return 'diesel' +'-'+d+'-'+ n;
                    },
                },
                { extend: 'pdfHtml5', footer: true,

                    text : 'Export to PDF',
                    filename: function(){
                        var d = new Date();
                        var n = d.getTime();
                        return 'diesel' +'-'+d+'-'+ n;
                    },
                    customize: function ( doc ) {
                        doc.watermark = {
                            text: 'Sadek Filling Station', color: 'blue', opacity: 0.1
                        };
                        doc.content.splice( 1, 0, {
                            margin: [ 0, 0, 0, 12 ],
                            alignment: 'center',
                            image: 'data:image/png;base64,'  } );

                    },
                    exportData: {decodeEntities:true},
                },
                {
                    extend: 'print',
                    customize: function ( win ) {
                        $(win.document.body)
                            .css( 'font-size', '10pt' )
                            .prepend(
                                '<img src="../../images/image_print.png" style="position:absolute; top:350; left:0;" />'
                            );

                        $(win.document.body).find( 'table' )
                            .addClass( 'compact' )
                            .css( 'font-size', 'inherit' );
                    },
                    footer:true,
                }

            ],
            // bProcessing: true,
            // bserverSide: true,
            ajax: {
                url:"diesel/ajaxperdaydieselsell.php",
                type:'POST',
                data:data,
            },
            columns: [
                { "data": "Date",
                    render: function ( data, type, row ) {
                        return dateformatChange(data);
                    }
                },
                { "data": "Sell-Quantity" ,
                    render: function ( data, type, row ) {
                        return data.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+'LTR';
                    }
                },
                { "data": "Buying-Rate",
                    render: function ( data, type, row ) {
                        return data.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³';
                    }
                },
                { "data": "Investment" ,
                    render: function ( data, type, row ) {
                        return data.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³';
                    }
                },
                { "data": "Sell-Rate" ,
                    render: function ( data, type, row ) {
                        return data.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³';
                    }
                },
                { "data": "Earn" ,
                    render: function ( data, type, row ) {
                        return data.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³';
                    }
                },
                { "data": "Profit",
                    render: function ( data, type, row ) {
                        return data.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³';
                    }
                },
            ],
            footerCallback: function( tfoot, data, start, end, display ) {
                var intVal = function ( i ) {
                    return typeof i === 'string' ?
                        i.replace(/[\$,]/g, '')*1 :
                        typeof i === 'number' ?
                            i : 0;
                };

                var api = this.api();
                $(api.column(1).footer()).html(
                    api.column(1).data().reduce(function ( a, b ) {
                        return intVal(a) + intVal(b);
                    }, 0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+'LTR'

                );
                $(api.column(3).footer()).html(
                   afterTwo(api.column(3).data().reduce(function ( a, b ) {
                        return intVal(a) + intVal(b);
                    }, 0)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³'

                );
                $(api.column(5).footer()).html(
                    afterTwo(api.column(5).data().reduce(function ( a, b ) {
                        return intVal(a) + intVal(b);
                    }, 0).toString()).replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³'

                );
                $(api.column(6).footer()).html(
                    afterTwo(api.column(6).data().reduce(function ( a, b ) {
                        return intVal(a) + intVal(b);
                    }, 0).toString()).replace(/\B(?=(\d{3})+(?!\d))/g, ",")+' à§³'

                );
            }
        });
    }



    $('#reservation').daterangepicker();
});